    hideInactivityWarning() {
      this.isInactivityWarningShown = false;
      this.showInactivityModal = false;
      if (this.inactivityTimerInterval) {
        clearInterval(this.inactivityTimerInterval);
        this.inactivityTimerInterval = null;
      }
    },
    continueSession() {
      this.updateLastActivity();
      this.hideInactivityWarning();
    },
    async logout() {
      try {
        await this.$store.dispatch('auth/logout');
        this.$router.push('/login');
      } catch (error) {
        console.error('Error during logout:', error);
      }
    },
    formatMessageWithLinks(text) {
      if (!text) return '';

      // –†–µ–≥—É–ª—è—Ä–Ω–æ–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ –¥–ª—è –ø–æ–∏—Å–∫–∞ URL
      const urlRegex = /(https?:\/\/[^\s<]+[^<.,:;"')\]\s])/g;

      // –ó–∞–º–µ–Ω—è–µ–º URL –Ω–∞ HTML-—Å—Å—ã–ª–∫–∏
      const formattedText = text.replace(urlRegex, url => {
        return `<a href="${url}" target="_blank" rel="noopener noreferrer" class="message-link">${url}</a>`;
      });

      // –≠–∫—Ä–∞–Ω–∏—Ä—É–µ–º HTML-—Ç–µ–≥–∏, –∫—Ä–æ–º–µ –Ω–∞—à–∏—Ö —Å—Å—ã–ª–æ–∫
      return this.escapeHTML(formattedText);
    },
    escapeHTML(text) {
      // –°–æ–∑–¥–∞–µ–º –≤—Ä–µ–º–µ–Ω–Ω—ã–π div –¥–ª—è –±–µ–∑–æ–ø–∞—Å–Ω–æ–≥–æ –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏—è —Ç–µ–∫—Å—Ç–∞
      const div = document.createElement('div');
      div.innerHTML = text;

      // –ù–∞—Ö–æ–¥–∏–º –≤—Å–µ —Å—Å—ã–ª–∫–∏, –∫–æ—Ç–æ—Ä—ã–µ –º—ã —Å–æ–∑–¥–∞–ª–∏
      const links = div.getElementsByTagName('a');

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å—Å—ã–ª–∫–∏ –≤–æ –≤—Ä–µ–º–µ–Ω–Ω—ã–π –º–∞—Å—Å–∏–≤
      const savedLinks = [];
      for (let i = 0; i < links.length; i++) {
        savedLinks.push(links[i].outerHTML);
      }

      // –≠–∫—Ä–∞–Ω–∏—Ä—É–µ–º –≤–µ—Å—å HTML
      let escapedText = div.textContent.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#039;');

      // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –Ω–∞—à–∏ —Å—Å—ã–ª–∫–∏
      savedLinks.forEach(link => {
        const placeholder = link.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#039;');
        escapedText = escapedText.replace(placeholder, link);
      });
      return escapedText;
    }
  }
};
