  },
  data() {
    return {
      messages: [],
      newMessage: '',
      polling: null,
      loading: false,
      error: null,
      showDeleteModal: false,
      messageToDelete: null,
      activeUsers: [],
      showContextMenu: false,
      contextMenuPosition: {
        x: 0,
        y: 0
      },
      selectedUser: null,
      activeChats: [],
      unreadCounts: {},
      unreadPolling: null,
      readMessagesByUser: {},
      showAllMessages: false,
      privateChats: [],
      showMentionPopup: false,
      mentionFilter: '',
      mentionStartIndex: -1,
      showEmojiPicker: false,
      currentCategory: 'smileys',
      emojiCategories: {
